  init = function () {
    var translated_paragraph = $('form .translation').closest('p');

    if (translated_paragraph.size() > 0) {
      memorize_ids();
      event_handler.init();

      translated_paragraph.closest('form').submit(function () {
        submit_preparer.prepare($(this));
        // allow default behaviour
      });

    }
  };

  return {
    init : init
  };
